    @Rule
    @JvmField
    val rule = InstantTaskExecutorRule()


    @Before
    fun setUp() {
        Dispatchers.setMain(Dispatchers.Unconfined)
        viewModel = SeriesDetailViewModel(
            seriesRepository
        )
        viewModel.isLoading().observeForever(observerLoading)
        viewModel.getActiveSeason().observeForever(observerActiveSeason)
        viewModel.getSeriesDetail().observeForever(observerSeriesDetail)
        viewModel.getEpisodes().observeForever(observerEpisode)
        viewModel.getErrorMessage().observeForever(observerErrorMessage)
        viewModel.isFavorited().observeForever(observerFav)
    }

